content:
/* General Styling */
.auth-box strong {
    color: #0077b6;
    font-weight: 600;
}
/* Style for the links */
.auth-box a {
    color: #0077b6;
    text-decoration: none;
    font-weight: 500;
    padding: 6px 12px;
    border-radius: 6px;
    transition: all 0.2s ease;
    background-color: transparent;
}
/* Special styling for Create Post link */
.auth-box a[href="/createPost"] {
    background-color: #0077b6;
    color: white;
}
.auth-box a[href="/createPost"]:hover {
    background-color: #005f7f;
    transform: translateY(-1px);
}
/* Style for Logout link */
.auth-box a[href="/logout"] {
    color: #dc3545;
}
.auth-box a[href="/logout"]:hover {
    background-color: #ffebee;
    color: #dc3545;
}
/* Regular link hover states */
.auth-box a:not([href="/createPost"]):not([href="/logout"]):hover {
    background-color: #e3f2fd;
}
/* Main Layout */
.main-container {
    display: flex;
    width: 85%;
    margin: 20px auto;
    gap: 20px;
}
/* Categories Sidebar */
.categories {
    background-color: white;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);
    width: 250px;
    height: fit-content;
}
