content:
/* General Styling */
.post-meta .right {
    display: flex;
    gap: 5px;
}
/* Like/Dislike Buttons */
button[name="type"] {
    background: none;
    border: none;
    cursor: pointer;
    font-size: 16px;
    padding: 5px;
    transition: transform 0.2s ease, opacity 0.2s ease;
}
button[name="type"]:hover {
    opacity: 0.7;
    transform: scale(1.1);
}
/* Comments */
.comment {
    background-color: #f9f9f9;
    border-left: 5px solid #4CAF50;
    padding: 15px 10px 15px 30px;
    margin-bottom: 15px;
    width: 100%;
    box-sizing: border-box;
    transition: all 0.3s ease;
}
.comment:hover {
    background-color: #f1f1f1;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    transform: translateX(5px);
}
/* Auth Forms */
.auth-form {
    max-width: 400px;
    margin: 0 auto;
    padding: 20px;
}
.auth-form h1 {
    text-align: center;
    color: #333;
    margin-bottom: 30px;
}
.auth-form label {
    display: block;
    margin-bottom: 8px;
    color: #555;
    font-weight: 500;
}
.auth-form input {
    width: 100%;
    padding: 12px;
    margin-bottom: 20px;
