content:
/* General Styling */
body {
    font-family: 'Poppins', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f4f4f4;
    color: #333;
    display: flex;
    flex-direction: column;
    align-items: center;
    min-height: 100vh;
    position: relative;
    padding-bottom: 80px;
}
body p {
    font-size: 20px; /* Adjust the size as needed */
}
/* Header Styling */
header {
    background: url('../image/aland.jpg') center/cover;
    height: 250px;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: flex-end;
    text-align: center;
    padding-bottom: 20px;
}
header h1 {
    color: white;
    font-size: 2.5rem;
    font-weight: bold;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.7);
}
/* Auth Links Box */
.auth-box {
    position: absolute;
    top: 15px;
    right: 20px;
    background-color: rgba(255, 255, 255, 0.95);
    padding: 12px 20px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.15);
    display: flex;
    align-items: center;
    gap: 15px;
    backdrop-filter: blur(5px);
}
/* Style for the username display */
.auth-box p {
    margin: 0;
    font-size: 0.95rem;
    color: #555;
}
