content:
/* General Styling */
.auth-form input:focus {
    outline: none;
    border-color: #008CBA;
    box-shadow: 0 0 5px rgba(0, 140, 186, 0.3);
}
.auth-form button {
    width: 100%;
    padding: 12px;
    background: #008CBA;
    color: white;
    border: none;
    border-radius: 5px;
    font-size: 16px;
    cursor: pointer;
    transition: background-color 0.3s ease;
    margin-bottom: 20px;
}
.auth-form button:hover {
    background: #005f7f;
}
.auth-form p {
    text-align: center;
    margin-top: 20px;
    color: #666;
}
.auth-form p a {
    color: #008CBA;
    text-decoration: none;
    font-weight: 500;
}
.auth-form p a:hover {
    text-decoration: underline;
}
.auth-form .error {
    background-color: #ffebee;
    color: #dc3545;
    padding: 12px;
    border-radius: 5px;
    margin-bottom: 20px;
    text-align: center;
}
/* Error Container */
.error-container {
    text-align: center;
    padding: 40px;
}
.error-container h1 {
    color: #dc3545;
    margin-bottom: 20px;
}
.error-container p {
    color: #666;
    margin-bottom: 30px;
    font-size: 18px;
}
/* Category Button Container */
.category-button {
    margin: 20px 0;
    text-align: center;
}
