content:
/* General Styling */
.form-container input:focus,
.form-container textarea:focus {
    outline: none;
    border-color: #0077b6;
    box-shadow: 0 0 5px rgba(0, 119, 182, 0.3);
}
.form-container button {
    width: 100%;
    padding: 10px;
    background: #008CBA;
    color: white;
    border: none;
    border-radius: 5px;
    font-size: 18px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}
.form-container button:hover {
    background: #005f7f;
}
/* Responsive Design */
@media (max-width: 768px) {
    .main-container {
        flex-direction: column;
        align-items: center;
        width: 95%;
    }

    .categories {
        width: 100%;
        text-align: center;
    }

    .auth-box {
        position: relative;
        top: auto;
        right: auto;
        margin: 10px auto;
        flex-wrap: wrap;
        justify-content: center;
        width: fit-content;
    }

    .auth-box p {
        width: 100%;
        text-align: center;
        margin-bottom: 8px;
    }

    .back-to-home {
        padding: 10px 20px;
        font-size: 14px;
    }

    .post-meta {
        flex-direction: column;
        gap: 10px;
    }

    .post-meta .right {
        justify-content: center;
    }
}
.checkbox-container {
    display: flex;
    flex-wrap: wrap; /* Allow wrapping if needed */
    gap: 15px; /* Space between each checkbox-label pair */
}
